import React, { useState } from 'react';
import ContadorCaracteres from './ContadorCaracteres';

function VerificacaoLogin() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
  };

  if (isAuthenticated) {
    return (
      <div>
        <h1>Bem-vindo ao contador de caracteres!</h1>
        <p>O usuário está autenticado.</p>
        <img src={require('./aprovado.png')} alt="autorizado" className="tamanho-img" />
        <ContadorCaracteres />
        <button onClick={handleLogout}>Logout</button>
      </div>
    );
  } else {
    return (
      <div>
        <h1>Usuário não está logado!</h1>
        <img src={require('./bloqueado.png')} alt="bloqueado" className="tamanho-img" />
        <p>Por favor, faça o login para acessar o contador de caracteres.</p>
        <button onClick={handleLogin}>Login</button>
      </div>
    );
  }
}

export default VerificacaoLogin;
